#!/usr/bin/env ruby18 require "shellwords" require ENV['TM_SUPPORT_PATH'] + '/lib/textmate'
puts `"${TM_RUBY:=ruby}" -e'puts "Using ruby-" + RUBY_VERSION.to_s'` result = `"${TM_RUBY:=ruby}" -wc 2>&1`
if result =~ /-:(\d+):(.*)/ TextMate.go_to :line => $1 puts $2 `#{ENV["TM_MATE"]} -c warning` result.each_line do |line| if line =~ /-:(\d+):(.*)/ `#{ENV["TM_MATE"]} --set-mark warning:#{Shellwords.escape($2)} --line #{$1}` end end else puts result `#{ENV["TM_MATE"]} -c warning` end
